import { request, response, Router } from "express";
import { methods as usuarioControllers} from "./../controllers/usuarioControllers";

const router = Router();

router.get("/", usuarioControllers.getUsuarios);
router.get("/:id", usuarioControllers.getUsuario);
router.post("/", usuarioControllers.addUsuarios);
router.put("/:id", usuarioControllers.updateUsuario);
router.delete("/:id", usuarioControllers.deleteUsuario);

export default router;

