import { getConnection } from "./../database/database"

//VER USUARIO
const getUsuarios = async (req, res) => {
    try {
        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM usuarios");
        console.log(result);
        res.json(result);
    }
    catch
    {
        res.send(500);
        res.send(error.messenge);
    }
};

//BUSCAR USUARIO POR ID
const getUsuario = async (req, res) => {
    try {
        const { id } = req.params;
        const connection = await getConnection();
        const result = await connection.query("SELECT id, Nombre, Correo, Contrasea FROM usuarios WHERE id = ?", id);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

//AADIR USUARIOS
const addUsuarios = async (req, res) => {

    try {
        const{Nombre, Correo, Contrasea} = req.body;

        if(Nombre == undefined || Correo == undefined || Contrasea== undefined){
            res.status(400).json({ message: "Bad Request. Please fill all field." });
        }
        const connection = await getConnection();
        await connection.query("INSERT INTO usuarios (Nombre,Correo, Contrasea) VALUE (?,?,?)", [Nombre, Correo, Contrasea]);
        res.json({message: "Usuario aadido"}); 
    }
    catch
    {
        res.send(500);
        res.send(error.messenge);
    }
};

//EDITAR USUARIOS
const updateUsuario = async (req, res) => {
    try {
        const { id } = req.params;
        const{Nombre, Correo, Contrasea} = req.body;

        if(id == undefined || Nombre == undefined || Correo == undefined || Contrasea== undefined){
            res.status(400).json({ message: "Bad Request. Please fill all field." });
        }
        const usuario ={Nombre, Correo, Contrasea}
        const connection = await getConnection();
         await connection.query("UPDATE usuarios SET ?  WHERE id = ?", [usuario, id]);
        res.json({message: "Usuario editado"});
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

//ELIMINAR USUARIO
const deleteUsuario = async (req, res) => {
    try {
        const { id } = req.params;
        const connection = await getConnection();
         await connection.query("DELETE FROM usuarios WHERE id = ?", id);
        res.json({message: "Usuario eliminado"});
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

export const methods ={
    getUsuarios,
    getUsuario,
    addUsuarios,
    deleteUsuario,
    updateUsuario
};

