import { getConnection } from "./../database/database"

//PAGINACION
const getProductoss = async (req, res) => {
        const {page} = req.params;
        const Paginas = 5;
        const skip = (page-1) * Paginas;
        const limite = skip + "," + Paginas;

        const connection = await getConnection();
      const rows = await connection.query("SELECT * FROM productos")
        const numRows = rows.length;
        const numPaginas = Math.ceil(numRows/Paginas);
        console.log(numPaginas)
        const result = await connection.query("SELECT * FROM productos Limit " + limite)
        res.status(200).json(result)
};


//VER PRODUCTO
const getProductos = async (req, res) => {
    try {
        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM productos");
        console.log(result);
        res.json(result);
    }
    catch
    {
        res.send(500);
        res.send(error.messenge);
    }
};

//BUSCAR PRODUCTO POR ID
const getProducto = async (req, res) => {
    try {
        const { id } = req.params;
        const connection = await getConnection();
        const result = await connection.query("SELECT Id_Categorias, Nombre, Descripcion, Precio FROM productos WHERE id = ?", id);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

//AADIR PRODUCTOS
const addProductos = async (req, res) => {

    try {
        const{Id_Categorias, Nombre, Descripcion, Precio} = req.body;

        if(Id_Categorias == undefined || Nombre == undefined || Descripcion== undefined || Precio== undefined){
            res.status(400).json({ message: "Bad Request. Please fill all field." });
        }
        const connection = await getConnection();
        await connection.query("INSERT INTO productos (Id_Categorias, Nombre, Descripcion, Precio) VALUE (?,?,?,?)", [Id_Categorias, Nombre, Descripcion, Precio]);
        res.json({message: "Producto aadido"}); 
    }
    catch
    {
        res.send(500);
        res.send(error.messenge);
    }
};

//EDITAR PRODUCTOS
const updateProducto = async (req, res) => {
    try {
        const { id } = req.params;
        const{Id_Categorias, Nombre, Descripcion, Precio} = req.body;

        if(Id_Categorias == undefined || Nombre == undefined || Descripcion== undefined || Precio== undefined){
            res.status(400).json({ message: "Bad Request. Please fill all field." });
        }
        const producto ={Id_Categorias, Nombre, Descripcion, Precio}
        const connection = await getConnection();
         await connection.query("UPDATE productos SET ?  WHERE id = ?", [producto, id]);
        res.json({message: "Producto editado"});
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

//ELIMINAR PRODUCTO
const deleteProducto = async (req, res) => {
    try {
        const { id } = req.params;
        const connection = await getConnection();
        await connection.query("DELETE FROM productos WHERE id = ?", id);
        res.json({message: "Usuario eliminado"});
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};




export const methods ={

    getProductoss,
    getProductos,
    getProducto,
    addProductos, 
    updateProducto,
    deleteProducto
    
};

