import express from "express";
import morgan from "morgan";

//Routes
import usuarios from "./routes/usuario.routes"
import productos from "./routes/producto.routes"

const app=express();

//setings
app.set("port", 4000);

//middlewares
app.use(morgan("dev"));
app.use(express.json( ));


//Routes
app.use("/bee_haank/usuario" ,usuarios);
app.use("/bee_haank/productos" ,productos);


export default app;

