import { getConnection } from "./../database/database"
const bcrypt = require("bcryptjs");

//VER USUARIO
const getUsuarios = async (req, res) => {
    try {
        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM usuario");
        console.log(result);
        res.json(result);
    }
    catch
    {
        res.send(500);
        res.send(error.messenge);
    }
};

//BUSCAR USUARIO POR ID
const getUsuario = async (req, res) => {
    try {
        const { id } = req.params;
        const connection = await getConnection();
        const result = await connection.query("SELECT id, Nombre, Correo_Electronico, Contrasea FROM usuario WHERE id = ?", id);
        res.json(result); 
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
}; 

//AADIR USUARIOS
const addUsuarios = async (req, res) => {

    
    try {
        const{Nombre, Correo_Electronico, Contrasea, Id_Tipo_Usuario} = req.body;
        let passwordHash = await bcrypt.hash(Contrasea, 8)
    
        if(Nombre == undefined || Correo_Electronico == undefined || Contrasea== undefined || Id_Tipo_Usuario == undefined){
            res.status(400).json({ message: "Bad Request. Please fill all field." });
        }
        const connection = await getConnection();
        await connection.query('INSERT INTO usuario SET ?', {Nombre : Nombre, Correo_Electronico : Correo_Electronico, Contrasea : passwordHash, Id_Tipo_Usuario : Id_Tipo_Usuario});
        res.json({message: "400"});
    }
    catch 
    {
        res.send(500); 
        res.send(error.messenge);
    }
}; 

//EDITAR USUARIOS
// const updateUsuario = async (req, res) => {
//     try {
//         const { id } = req.params;
//         const{Nombre, Correo_Electronico, Contrasea} = req.body;

//         if(id == undefined || Nombre == undefined || Correo_Electronico == undefined || Contrasea== undefined){
//             res.status(400).json({ message: "Bad Request. Please fill all field." });
//         }
//         const usuario ={Nombre, Correo_Electronico, Contrasea}
//         const connection = await getConnection();
//         await connection.query("UPDATE usuario SET ?  WHERE id = ?", [usuario, id]);
//         res.json({message: "Usuario editado"});
//     } catch (error) {
//         res.status(500);
//         res.send(error.message);
//     }
// };

//ELIMINAR USUARIO
const deleteUsuario = async (req, res) => {
    try {
        const { id } = req.params;
        const connection = await getConnection();
        await connection.query("DELETE FROM usuario WHERE id = ?", id);
        res.json({message: "Usuario eliminado"});
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

const login = async (req, res) => {

        const { Correo_Electronico } = req.body;
        const { Contrasea } = req.body;
        const connection = await getConnection();
        const [resultado] = await connection.query("SELECT * FROM usuario WHERE Correo_Electronico = ?", Correo_Electronico);
        const compare =  await bcrypt.compare(Contrasea, resultado.Contrasea)
        console.log(resultado.Contrasea)
        console.log(req.body)
        console.log(resultado)
        console.log(compare)
        if(!compare) return res.json("mal")
        return res.json("entro")
};

export const methods ={
    getUsuarios,
    getUsuario,
    addUsuarios,
    deleteUsuario,
    // updateUsuario,
    login
};

