import { request, response, Router } from "express";
import { methods as productoControllers} from "./../Controllers/productoControllers";

const router = Router();

    router.get("/productos/:page", productoControllers.getProductoss);
    router.get("/", productoControllers.getProductos);
    router.get("/:id", productoControllers.getProducto);
    router.post("/", productoControllers.addProductos); 
    router.put("/:id", productoControllers.updateProducto); 
    router.delete("/:id", productoControllers.deleteProducto);

export default router;

