import { token } from "morgan";
import { getConnection } from "./../database/database"
const bcrypt = require("bcryptjs");
const jwt = require('jsonwebtoken')

//VER USUARIO
const getUsuarios = async (req, res) => {
    try {
        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM usuario");
        console.log(result);
        res.json(result);
    }
    catch
    {
        res.send(500);
        res.send(error.messenge);
    }
};

//BUSCAR USUARIO POR ID
const getUsuario = async (req, res) => {
    try {
        const { id } = req.params;
        const connection = await getConnection();
        const result = await connection.query("SELECT id, Nombre, Correo_Electronico, Contrasea FROM usuario WHERE id = ?", id);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

//AADIR USUARIOS
const addUsuarios = async (req, res) => {

    try {
        const{Nombre, Correo_Electronico, Contrasea, Id_TipoUsuario} = req.body;
        let passwordHash = await bcrypt.hash(Contrasea, 8)
    
        if(Nombre == undefined || Correo_Electronico == undefined || Contrasea== undefined || Id_TipoUsuario == undefined){
            res.status(400).json({ message: "Bad Request. Please fill all field." });
        }
        const connection = await getConnection();
        await connection.query('INSERT INTO usuario SET ?', {Nombre : Nombre, Correo_Electronico : Correo_Electronico, Contrasea : passwordHash, Id_TipoUsuario : Id_TipoUsuario});
        const user = await  connection.query('SELECT * FROM usuario WHERE Correo_Electronico = ?', Correo_Electronico)
        const idUser = user[0].Id; 
        console.log("este es el id del usuario: " + idUser); 

        const token = await jwt.sign({Id : idUser}, JWT_TOKEN, {expiresIn: '1h'});

        const data = {
            token: token,
            user: user
        }

        console.log(token);
        res.send(data)
    }
    catch
    {
        res.send(500);
        res.send(error.messenge);
    }
}; 

//ELIMINAR USUARIO
const deleteUsuario = async (req, res) => {
    try {
        const { id } = req.params;
        const connection = await getConnection();
        await connection.query("DELETE FROM usuario WHERE id = ?", id);
        res.json({message: "Usuario eliminado"});
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

const login = async (req, res) => {

        const { Correo_Electronico } = req.body;
        const { Contrasea } = req.body;
        const connection = await getConnection();
        const [resultado] = await connection.query("SELECT * FROM usuario WHERE Correo_Electronico = ?", Correo_Electronico);
        const compare =  await bcrypt.compare(Contrasea, resultado.Contrasea) //devuelve true
        console.log("es true?: ", compare); 
        console.log(resultado.Contrasea)
        console.log(req.body) 
        console.log(resultado); 
        console.log(compare)
        if(compare) {
            const usuarioAutentificado = await verifyToken(req, res)
            return res.json(`BIENVENIDO ${JSON.stringify(usuarioAutentificado[0].Nombre).toUpperCase()}`);
        }
        else{  
            return res.json("mal")
        }
};//eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJJZCI6NzIsImlhdCI6MTY2OTI5OTE2MCwiZXhwIjoxNjY5MzAyNzYwfQ.y-qzqbzXOF2SwK-AS2HqQvLEykgJs7yQZJ5CbiLDZdU

const JWT_TOKEN = process.env.JWT_TOKEN;

const verifyToken = async (req, res) => {
    const token = req.headers.authorization.split(" ").pop(); 
    if (token) {
        try {
            const decodificada = await jwt.verify(token, JWT_TOKEN)

            console.log("que hay aqui", decodificada); //debe devolver un id 
            console.log("id decodificado; ", decodificada.Id);

            const connection = await getConnection();
            const user = await connection.query('SELECT * FROM usuario WHERE Id = ?', decodificada.Id) 
            console.log("usuario de token:", user);

            return user;

        } catch (error) {
            console.log(error)
        }
    }else{
        res.json("sin token")   
        return null;  
    }
};

export const methods ={
    getUsuarios,
    getUsuario,
    addUsuarios,
    deleteUsuario,
    login,
    verifyToken
};

