import { getConnection } from "../database/database"
const bcrypt = require("bcryptjs");
const jwt = require('jsonwebtoken');


const login = async (req, res) => {

        const { Correo_Electronico } = req.body;   
        const { Contrasea } = req.body;
        const connection = await getConnection();
        const [resultado] = await connection.query("SELECT * FROM usuario WHERE Correo_Electronico = ?", Correo_Electronico);
        const compare =  await bcrypt.compare(Contrasea, resultado.Contrasea) //devuelve true
        console.log("es true?: ", compare); 
        console.log("Datos ingresados",req.body) 
        console.log("resultados",resultado); 
        console.log("contrasenia es...", compare)
        if(compare) {
            await verifyToken(req, res)
            return res.json("BIENVENIDOS");
        }   
        else{   
            return res.json("mal") 
        } 
};
///ingresa con un usario que tenga permisos para ver los productos, dame chance



const verifyToken = async (req, res) => {
    const JWT_TOKEN = process.env.JWT_TOKEN;
    if (req.headers.authorization) {
        const token = req.headers.authorization.split(" ").pop();
    
    
    if (token) {
        try {
            const decodificada = await jwt.verify(token, JWT_TOKEN)

            const connection = await getConnection();
            const user = await connection.query('SELECT * FROM usuario WHERE Id = ?', decodificada.Id) 

            return user; 

        } catch (error) {
            console.log(error)
        }
    }else{
        res.json("sin token")   
        return null;  
    }
};
}
export const methods ={ 
    login,
    verifyToken
};

