import { getConnection } from "./../database/database"

//PAGINACION
const getProductoss = async (req, res) => {
    const {page} = req.params;
    const Paginas = 5;
    const skip = (page-1) * Paginas;
    const limite = skip + "," + Paginas;
    
    const connection = await getConnection();
    const rows =   await connection.query("SELECT * FROM producto")
    const numRows = rows.length;
    const numPaginas = Math.ceil(numRows/Paginas);
    console.log(numPaginas)
    const result = await connection.query("SELECT * FROM producto Limit " + limite)
    res.status(200).json(result)
};

//VER PRODUCTO
const getProductos = async (req, res) => {
    try {
        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM producto");
        console.log(result);
        res.json(result); 
    }
    catch
    {
        res.send(500);
        res.send(error.messenge);
    }
};

//BUSCAR PRODUCTO POR ID
const getProducto = async (req, res) => {
    try {
        const { Id_producto } = req.params;
        const connection = await getConnection();
        const result = await connection.query("SELECT Id_Categoria, Nombre_Producto, Descripcion_Producto, Costo_Producto, Existencias, Imagen FROM productos WHERE id = ?", id);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

//AADIR PRODUCTOS
const addProductos = async (req, res) => {

    try {
        const{Id_Categoria, Nombre_Producto, Descripcion_Producto, Costo_Producto, Existencias, Imagen } = req.body;

        if(Id_Categoria == undefined || Nombre_Producto == undefined || Descripcion_Producto == undefined || Costo_Producto == undefined || Existencias == undefined || Imagen == undefined){
            res.status(400).json({ message: "Bad Request. Please fill all field." });
        }
        const connection = await getConnection();
        await connection.query("INSERT INTO productos (Id_Categoria, Nombre_Producto, Descripcion_Producto, Costo_Producto, Existencias, Imagen) VALUE (?,?,?,?,?,?)", [Id_Categoria, Nombre_Producto, Descripcion_Producto, Costo_Producto, Existencias, Imagen]);
        res.json({message: "Producto aadido"}); 
    }
    catch
    {
        res.send(500);
        res.send(error.messenge);  
    }
};

//EDITAR PRODUCTOS
const updateProducto = async (req, res) => {
    try {
        const { Id_producto } = req.params;
        const{Id_Categoria, Nombre_Producto, Descripcion_Producto, Costo_Producto, Existencias, Imagen } = req.body;

        if(Id_Categoria == undefined || Nombre_Producto == undefined || Descripcion_Producto == undefined || Costo_Producto == undefined || Existencias == undefined || Imagen == undefined){
            res.status(400).json({ message: "Bad Request. Please fill all field." });
        }
        const producto ={Id_Categorias, Nombre, Descripcion, Precio}
        const connection = await getConnection();
         await connection.query("UPDATE productos SET ?  WHERE id = ?", [producto, id]);
        res.json({message: "Producto editado"});
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

//ELIMINAR PRODUCTO
const deleteProducto = async (req, res) => {
    try {
        const { Id_producto } = req.params;
        const connection = await getConnection();
        await connection.query("DELETE FROM productos WHERE id = ?", id);
        res.json({message: "Usuario eliminado"});
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};


//BUSQUEDA POR NOMBRE

const name = async (req, res) => {

    const { Nombre_Producto } = req.body
    const connection = await getConnection();
    const resultado = await connection.query("SELECT * FROM producto WHERE Nombre_Producto = ?", Nombre_Producto);
    if(resultado.length <=0) res.json(404) 
    else{
    res.json(resultado)}
};

//BUSQUEDA POR CATALOGO
const catalogo = async (req, res) => {

    const { Id_Categoria } = req.body;
    const connection = await getConnection();
    const [resultado] = await connection.query("SELECT * FROM producto WHERE Id_Categoria = ?", Id_Categoria);
    if(resultado.length <=0) res.json(404)
    else{
    res.json(resultado)}
}; 
//filtrado

const filtrado = async (req, res) => {

    try {
        const {precio} = req.params
        const limit = precio
        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM producto WHERE Costo_Producto <="+ limit)
        res.status(200).json(result)
    }
    catch
    {
        res.send(500);
        res.send(error.messenge);
    }
};

export const methods ={

    getProductoss,
    getProductos,
    getProducto,
    addProductos, 
    updateProducto,
    deleteProducto,
    name,
    catalogo,
    filtrado
    
};

