sourcesContent:
import { methods as verifyToken }  from "../Controllers/loginControllers"

const checkAuth = async (req, res, next) => {
    try {
        
        const token = req.headers.authorization.split(' ').pop() 
        const tokenData = await verifyToken(token)//no es este?
        console.log(token)
        console.log(tokenData.Id[0])
        console.log(tokenData)
 
       
        if (tokenData) { 
            next()
        } else {
            res.status(409)
            res.send({ error: 'Tu por aqui no pasas!' })
        }

    } catch (e) {
        console.log(e)
        res.status(409)
        res.send({ error: 'Tu por aqui no pasaz!' })
    }
        
}


export const admin = (req, res, next) => {
    const token = req.headers.authorization.split(' ').pop() 
 
    const JWT_TOKEN = process.env.JWT_TOKEN;
    const decoded = jwt.verify(token, JWT_TOKEN);

    if ([1].includes(decoded.rol)){
        res.status(200);
        next();
        } else {
            res.status(401).send("Acceso denegado");
        }
}

module.exports = checkAuth

