sourcesContent:
import {  Router } from "express";
import { methods as productoControllers} from "./../Controllers/productoControllers";
const checkAuth = require('../middleware/auth')
const router = Router();

    router.get("/productos/:page", productoControllers.getProductoss);//PAGINACION

    router.get("/", checkAuth,productoControllers.getProductos);//VER PRODUCTO      prueba en este
    router.get("/:id", productoControllers.getProducto);//BUSCAR PRODUCTO POR ID
    router.post("/", productoControllers.addProductos); //AADIR PRODUCTOS
    router.put("/:id", productoControllers.updateProducto); //EDITAR PRODUCTOS
    router.delete("/:id", productoControllers.deleteProducto);//ELIMINAR PRODUCTO
    router.post("/busqueda", productoControllers.name);//BUSQUEDA POR NOMBRE
    router.post("/busquedas", productoControllers.catalogo);//BUSQUEDA POR CATALOGO
    router.get("/filtrado/:precio", productoControllers.filtrado);//filtrado

export default router;

