import { Router } from "express";
import { methods as carritoControllers} from "./../Controllers/carritoController";
const checkAuth = require('../middleware/auth')

const router = Router();

    router.post("/", checkAuth,carritoControllers.aniadir);
    router.put("/:id", carritoControllers.cambiarCantidad);

export default router;

