sourcesContent:
import { getConnection } from "./../database/database"
const { verifyToken } = require('../helpers/generateToken')

//Agregar al carrito
const aniadir = async (req, res) => {

    try {
        const{Id_Producto, Cantidad } = req.body;

        if(Id_Producto == undefined || Cantidad == undefined){
            res.status(400).json({ message: "Bad Request. Please fill all field." });
        }
        const token = req.headers.authorization.split(' ').pop() 
        const tokenData = await verifyToken(token)
        const connection = await getConnection();
        await connection.query('INSERT INTO carrito SET ?', {Id_Producto : Id_Producto, Cantidad : Cantidad, id_usuario : tokenData.id});
        res.json({message: "Producto aadido"}); 
    } 
    catch
    {
        res.send(500);
        res.send(error.messenge);  
    }
};

//Cambiar cantidad

const cambiarCantidad = async (req, res) => {
    try {
        const { id } = req.params;
        const{Cantidad} = req.body;

        if(Cantidad == undefined){
            res.status(400).json({ message: "Bad Request. Please fill all field." });
        }
        const producto ={Cantidad}
        const connection = await getConnection();
        await connection.query("UPDATE carrito SET ?  WHERE Id = ?", [producto, id]);
        const result = await connection.query("SELECT Id_Producto, Cantidad, id_usuario FROM carrito WHERE Id = ?", id);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};


export const methods ={ 
    aniadir,
    cambiarCantidad
};

