sourcesContent:
import express from "express";
import morgan from "morgan";

//Routes
import usuarios from "./routes/usuario.routes"
import productos from "./routes/producto.routes"
import login from "./routes/login.routes"
import signUp from "./routes/signUp.routes"
import carrito from "./routes/carrito.routes"

const app=express();

//setings 
app.set("port", 4000);
 
//middlewares
app.use(morgan("dev"));
app.use(express.json( ));

  
//Routes
app.use("/bee/usuario" ,usuarios);
app.use("/bee/productos" ,productos);
app.use("/bee/login" ,login);//entrar usuario
app.use("/bee/signUp" ,signUp);//crear usuariologin
app.use("/bee/carrito" ,carrito);
   

export default app;

