sourcesContent:
import {  Router } from "express";
import { methods as productoControllers} from "./../Controllers/productoControllers";
const checkAuth = require('../middleware/auth')
const router = Router();

    router.get("/productos/:page", productoControllers.paginacion);//PAGINACION

    router.get("/", productoControllers.verProductos);//VER PRODUCTO
    router.get("/:id", productoControllers.buscarId);//BUSCAR PRODUCTO POR ID
    router.post("/", productoControllers.aniadirProducto); //AADIR PRODUCTOS
    router.put("/:id", productoControllers.editarProducto); //EDITAR PRODUCTOS
    router.delete("/:id", productoControllers.eliminarProducto);//ELIMINAR PRODUCTO
    router.post("/busqueda", productoControllers.name);//BUSQUEDA POR NOMBRE
    router.post("/busquedas", productoControllers.catalogo);//BUSQUEDA POR CATALOGO
    router.get("/filtrado/:precio", productoControllers.filtrado);//filtrado

export default router;

