sourcesContent:
import { getConnection } from "../database/database"
const bcrypt = require("bcryptjs");


//AADIR USUARIOS
const addUsuarios = async (req, res) => {

    try {
        const{Nombre, Correo_Electronico, Contrasea} = req.body;
        let passwordHash = await bcrypt.hash(Contrasea, 8)
    
        if(Nombre == undefined || Correo_Electronico == undefined || Contrasea== undefined ){
            res.status(400).json({ message: "Bad Request. Please fill all field." });
        }
        const connection = await getConnection();
        await connection.query('INSERT INTO usuario SET ?', {Nombre : Nombre, Correo_Electronico : Correo_Electronico, Contrasea : passwordHash, Roles : "2"});
        const user = await  connection.query('SELECT * FROM usuario WHERE Correo_Electronico = ?', Correo_Electronico)
        const idUser = user[0].Id; 
        console.log("este es el id del usuario: " + idUser); 



        const data = {
            user: user
        }


        res.send(data)
    }
    catch
    {
        res.send(500);
        res.send(error.messenge);
    }
}; 


export const methods ={
    addUsuarios
};

